def test_match():
    result = ManilaConf(context_wrap(manila_content, osp=osp))
    assert result.data.get('DEFAULT', 'share_topic') == 'manila-share'
    assert result.data.get('DEFAULT', 'scheduler_topic') == 'manila-scheduler'
